package com.telcobright.statemachineexamples.smsmachine.events;

import com.telcobright.statemachine.events.StateMachineEvent;

/**
 * Retry event for SmsMachine
 * Generated by State Machine Scaffolder
 */
public class Retry implements StateMachineEvent {
    
    public static final String EVENT_TYPE = "RETRY";
    
    private final Object payload;
    private final long timestamp;
    
    public Retry() {
        this(null);
    }
    
    public Retry(Object payload) {
        this.payload = payload;
        this.timestamp = System.currentTimeMillis();
    }
    
    @Override
    public String getEventType() {
        return EVENT_TYPE;
    }
    
    @Override
    public String getDescription() {
        return "Retry event" + (payload != null ? " with payload: " + payload : "");
    }
    
    @Override
    public Object getPayload() {
        return payload;
    }
    
    @Override
    public long getTimestamp() {
        return timestamp;
    }
}